// 引入Count的UI组件
import CountUI from '../../components/Count'
// 引入store
// import store from '../../redux/store'
// 引用connect用于连接UI组件与redux
import {connect} from 'react-redux'
// a函数返回的对象的KEY就作为传递给UI组件props的key,value就作为爨地给UI组件props的value---状态
function a(state) {
    return{count:state}
}
// a函数返回的对象的KEY就作为传递给UI组件props的key,value就作为爨地给UI组件props的value---操作状态的方法

function b() {
    return{jia:(number)=>{
        // 通知redux执行加法
        store.dispatch({type:'increment',data:number})
    }}
    
}


//使用connect()()创建并暴露一个Count的容器组件
export default  connect(a,b)(CountUI)
