/*
该文件专门用于store对象，整个应用只有一个store对象
*/ 


// 引入createStore，专门用于创建redux中最为核心的store对象     applyMiddleware这个要强行记忆
import {createStore,applyMiddleware,combineReducers} from 'redux'
// 引入Count组件服务的reducer
import countReducer from './reducers/count'
import personReducer from './reducers/person'
// 引入 redux-thunk,用于支持异步action
import thunk from 'redux-thunk'
// combineReducers调用时传入的对象就是redux帮我们保存的总保存对象
// 汇总所有的reducer
const allReducer =combineReducers({
    he:countReducer,
    rens:personReducer

})
// 暴露store
export default createStore(allReducer,countReducer,applyMiddleware(thunk))
